//================================================================================
//=========================================================================== INIT
//================================================================================
//=========================================================================== INIT
void inicializa(void)
{
    glClearColor(BLACK);        //………………………………………………………………………………Apagar
    glEnable(GL_DEPTH_TEST);    //………………………………………………………………………………Profundidade
    glShadeModel(GL_SMOOTH);    //………………………………………………………………………………Interpolacao de cores
    
    glEnable(GL_CULL_FACE);        //………………………………………………………………………………Faces visiveis
    glCullFace(GL_BACK);        //………………………………………………………………………………Mostrar so as da frente
    
    glVertexPointer(3, GL_FLOAT, 0, vertices); //………………………………………Vertex arrays
    glEnableClientState(GL_VERTEX_ARRAY);
    glNormalPointer(GL_FLOAT, 0, normais);
    glEnableClientState(GL_NORMAL_ARRAY);
    glColorPointer(3, GL_FLOAT, 0, cores);
    glEnableClientState(GL_COLOR_ARRAY);
    
    
}



void drawEixos()
{
    //~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~Eixo X
    glColor4f(RED);
    glBegin(GL_LINES);
    glVertex3i( 0, 0, 0);
    glVertex3i(10, 0, 0);
    glEnd();
    //~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~Eixo Y
    glColor4f(GREEN);
    glBegin(GL_LINES);
    glVertex3i(0,  0, 0);
    glVertex3i(0, 10, 0);
    glEnd();
    //~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~Eixo Z
    glColor4f(BLUE);
    glBegin(GL_LINES);
    glVertex3i( 0, 0, 0);
    glVertex3i( 0, 0,10);
    glEnd();
    
}




void drawScene(){
    int i;
    //=================================================== Qual o lado ?
    if (frenteVisivel)
        glCullFace(GL_BACK);  //glFrontFace(GL_CW);
    else
        glCullFace(GL_FRONT);  //glFrontFace(GL_CCW);
    
    
    //==================================== MESA
    //glColorPointer(3, GL_FLOAT, 0, cor);     podia ser modificada a cor !
    for(i=1;i<=23;i++){
        glPushMatrix();
        esquerda[0]=4*i;
        esquerda[1]=4*i+1;
        esquerda[2]=4*i+2;
        esquerda[3]=4*i+3;
        glDrawElements(GL_POLYGON, 4, GL_UNSIGNED_INT, esquerda);
        glPopMatrix();
    }

    
    
    //==================================== PAralelipipedo Amarelo
    glColor4f(YELLOW);
    glPushMatrix();
    //?? escala, rotacao, translacao ??
    //glutWireTeapot(1);
    
    glPopMatrix();
    
}

void display(void){
    
    //================================================================= APaga ecran/profundidade
    glClear(GL_COLOR_BUFFER_BIT | GL_DEPTH_BUFFER_BIT );
    
    //glOrtho(-400, 400, -400, 400, -400, 400);

    //================================================================= NAO MOFIFICAR
    glViewport (0, 0, wScreen, hScreen);                                // ESQUECER PoR AGORA
    glMatrixMode(GL_PROJECTION);                                        // ESQUECER PoR AGORA
    glLoadIdentity();                                                    // ESQUECER PoR AGORA
    gluPerspective(angZoom, (float)wScreen/hScreen, 0.1, 30*zC);            // ESQUECER PoR AGORA
    glMatrixMode(GL_MODELVIEW);                                            // ESQUECER PoR AGORA
    glLoadIdentity();                                                    // ESQUECER PoR AGORA
    //================================================================= NAO MOFIFICAR
    
    
    //-------------------------------------------------------------- observador
    //gluLookAt(obsP[0], obsP[1], -50, 0,0,0, 0, 1, 0);
    
    gluLookAt(0, 0, -50, 0,0,50, 0, 1, 0);
    //…………………………………………………………………………………………………………………………………………………………Objectos/modelos
    drawEixos();
    drawScene();
    
    //~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~Actualizacao
    glutSwapBuffers();
}


//======================================================= EVENTOS
void keyboard(unsigned char key, int x, int y){
    
    
    switch (key) {
        case 'f':
        case 'F':
            frenteVisivel=!frenteVisivel;
            glutPostRedisplay();
            break;
            
        case 'A':
        case 'a':
            //??
            glutPostRedisplay();
            break;
            
        case 'S':
        case 's':
            //??
            glutPostRedisplay();
            break;
            
        case 'e':
        case 'E':
            //??
            glutPostRedisplay();
            break;
            
        case 'd':
        case 'D':
            //??
            glutPostRedisplay();
            break;
            //--------------------------- Escape
        case 27:
            exit(0);
            break;
    }
    
}



void teclasNotAscii(int key, int x, int y){
    
    if(key == GLUT_KEY_UP)
        obsP[1] = (obsP[1]+ 0.5) ;
    if(key == GLUT_KEY_DOWN)
        obsP[1] = (obsP[1]- 0.5) ;
    
    if (obsP[1]>yC)
        obsP[1]=yC;
    if (obsP[1]<-yC)
        obsP[1]=-yC;
    
    if(key == GLUT_KEY_LEFT)
        aVisao = (aVisao + 0.5) ;
    if(key == GLUT_KEY_RIGHT)
        aVisao = (aVisao - 0.5) ;
    
    obsP[0]=rVisao*cos(aVisao);
    obsP[2]=rVisao*sin(aVisao);
    
    
    glutPostRedisplay();
    
}


//======================================================= MAIN
//======================================================= MAIN
int main(int argc, char** argv){
    
    glutInit(&argc, argv);
    glutInitDisplayMode (GLUT_DOUBLE | GLUT_RGB | GLUT_DEPTH );
    glutInitWindowSize (wScreen, hScreen);
    glutInitWindowPosition (300, 100);
    glutCreateWindow ("{jh,pjmm}@dei.uc.pt|       |FaceVisivel:'f'|      |Observador:'SETAS'|        |Andar-'a/s'|        |Rodar -'e/d'| ");
    
    inicializa();
    
    glutSpecialFunc(teclasNotAscii);
    glutDisplayFunc(display);
    glutKeyboardFunc(keyboard);
    
    glutMainLoop();
    
    return 0;
}



